import React from "react";
import "./Portfolio.css";
import { Link } from "react-router-dom";
import { useState } from "react";
// import "react-bootstrap";
// import "bootstrap/dist/css/bootstrap.min.css";
import informedDecisionImage from "./img/undraw_informed_decision_p2lh.svg";
export default function Portfolio() {
  const [isHovered, setIsHovered] = useState(false);
  const style = {
    margin: 5,
    fontSize: "1.3rem",
    fontWeight: "bold",
    // textDecoration: "none",
  };
  const st = {
    textDecoration: "none",
    backgroundColor: isHovered ? "lightgray" : "transparent",
  };
  const containerStyle = {
    // border: "1px solid red",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start",
  };
  const columnStyle = {
    width: "calc(50% - 5px)",
    boxSizing: "border-box",
    overflow: "hidden",
    border: "1px solid red",
    height: "650px",
    // Subtracting the border width from 50% to maintain even space between columns
  };
  return (
    <div className="portfolio">
      <div
        className="head"
        style={{
          //   border: "1px solid white",
          display: "flex",
          justifyContent: "space-around",
        }}
      >
        <h2 style={{ marginRight: "800px" }}>DEVOPS Pvt LTD</h2>
        <ul style={{ display: "flex" }}>
          {/* <li className="" style={style}>
            <a
              style={st}
              href=""
              className="home"
             
            >
              Home
            </a>
          </li> */}
          {/* <li style={style}>
            <a style={st} href="">
              About
            </a>
          </li>
          <li style={style}>
            <a style={st} href="">
              Service
            </a>
          </li>
          <li style={style}>
            <a style={st} href="">
              Contact
            </a>
          </li> */}
          <li style={style}>
            <a style={st} href="">
              <Link style={st} to="/login">
                Login
              </Link>
            </a>
          </li>
        </ul>
      </div>
      <div style={containerStyle}>
        <div style={columnStyle}>
          <div
            style={{
              //   border: "1px solid blue",
              height: "100%",
              alignItems: "center",
            }}
          >
            <header>
              <h1>Welcome to Task Management</h1>
              <p>
                Your ultimate solution for organizing tasks and boosting
                productivity
              </p>
            </header>
            <section className="features">
              <div className="feature">
                <h2>Task Organization</h2>
                <p>
                  Easily create, categorize, and prioritize your tasks for
                  efficient management.
                </p>
              </div>
              <div className="feature">
                <h2>Collaboration</h2>
                <p>
                  Invite team members, assign tasks, and track progress
                  collaboratively.
                </p>
              </div>
              <div className="feature">
                <h2>Reminders and Notifications</h2>
                <p>
                  Stay on top of your tasks with reminders and notifications,
                  ensuring nothing falls through the cracks.
                </p>
              </div>
            </section>
            <section className="call-to-action">
              <h2>Get Started Today</h2>
              <p>
                Sign up now to streamline your task management process and
                achieve your goals effortlessly.
              </p>
              <button>Sign Up</button>
            </section>
          </div>
          {/* <p>psatdfadsjbkhfb jhsdbfksydgfsd</p> */}
          {/* <img src={informedDecisionImage} alt="Informed Decision" /> */}
        </div>
        <div style={columnStyle}>
          {/* <p>jfgbskeudfgwuglui</p> */}
          <img src={informedDecisionImage} alt="Informed Decision" />
        </div>
      </div>
    </div>
  );
}
