import { onResize } from '../resize'

import { ScrollInfo, ScrollHandler } from './ScrollHandler'

export type OnScrollCallback = (info: ScrollInfo) => void

export type OnScrollOptions = {
  /**
   * The root container to measure against
   */
  container?: HTMLElement
}

const scrollListeners = new WeakMap<Element, () => boolean>()
const resizeListeners = new WeakMap<Element, VoidFunction>()
const onScrollHandlers = new WeakMap<Element, Set<ScrollHandler>>()

const getTarget = (container: HTMLElement) =>
  container === document.documentElement ? window : container

export const onScroll = (
  callback: OnScrollCallback,
  { container = document.documentElement }: OnScrollOptions = {}
) => {
  /**
   * get the current handlers for the target
   */
  let containerHandlers = onScrollHandlers.get(container)

  /**
   * If there aren't any handlers then create a new set.
   */
  if (!containerHandlers) {
    containerHandlers = new Set()
    onScrollHandlers.set(container, containerHandlers)
  }

  /**
   * Create a new ScrollHandler class and add it to the set.
   */
  const containerHandler = new ScrollHandler(callback, container)
  containerHandlers.add(containerHandler)

  /**
   * If there are no scrollListeners then we need to make them
   */
  if (!scrollListeners.has(container)) {
    /**
     * Return true so RAFZ continues to run it
     */
    const listener = () => {
      containerHandlers?.forEach(handler => handler.advance())
      return true
    }

    scrollListeners.set(container, listener)

    const target = getTarget(container)

    /**
     * Add resize handlers so we can correctly calculate the
     * scroll position on changes
     */
    window.addEventListener('resize', listener, { passive: true })

    if (container !== document.documentElement) {
      resizeListeners.set(container, onResize(listener, { container }))
    }

    /**
     * Add the actual scroll listener
     */
    target.addEventListener('scroll', listener, { passive: true })
  }

  /**
   * Start animation loop
   */
  const animateScroll = scrollListeners.get(container)!
  raf(animateScroll)

  return () => {
    /**
     * Clear it on cleanup
     */
    raf.cancel(animateScroll)

    /**
     * Check if we even have any handlers for this container.
     */
    const containerHandlers = onScrollHandlers.get(container)
    if (!containerHandlers) return

    containerHandlers.delete(containerHandler)

    if (containerHandlers.size) return

    /**
     * If no more handlers, remove the scroll listener too.
     */
    const listener = scrollListeners.get(container)
    scrollListeners.delete(container)

    if (listener) {
      getTarget(container).removeEventListener('scroll', listener)
      window.removeEventListener('resize', listener)

      resizeListeners.get(container)?.()
    }
  }
}
import { useRef } from 'react'

type Init<T> = () => T

/**
 * Creates a constant value over the lifecycle of a component.
 */
export function useConstant<T>(init: Init<T>) {
  const ref = useRef<T | null>(null)

  if (ref.current === null) {
    ref.current = init()
  }

  return ref.current
}
import { useState } from 'react'
import { useIsMounted } from './useIsMounted'

/** Return a function that re-renders this component, if still mounted */
export function useForceUpdate() {
  const update = useState<any>()[1]
  const isMounted = useIsMounted()
  return () => {
    if (isMounted.current) {
      update(Math.random())
    }
  }
}
import { useRef } from 'react'
import { useIsomorphicLayoutEffect } from './useIsomorphicLayoutEffect'

export const useIsMounted = () => {
  const isMounted = useRef(false)
  useIsomorphicLayoutEffect(() => {
    isMounted.current = true

    return () => {
      isMounted.current = false
    }
  }, [])

  return isMounted
}
import { useEffect, useLayoutEffect } from 'react'

import { isSSR } from '../helpers'

/**
 * Use this to read layout from the DOM and synchronously
 * re-render if the isSSR returns true. Updates scheduled
 * inside `useIsomorphicLayoutEffect` will be flushed
 * synchronously in the browser, before the browser has
 * a chance to paint.
 */
export const useIsomorphicLayoutEffect = isSSR() ? useEffect : useLayoutEffect
import { useEffect, useRef, useState } from 'react'

type Cache<T> = {
  inputs?: any[]
  result?: T
}

// TODO: remove once merged (https://github.com/alexreardon/use-memo-one/pull/10)
export function useMemoOne<T>(getResult: () => T, inputs?: any[]): T {
  const [initial] = useState(
    (): Cache<T> => ({
      inputs,
      result: getResult(),
    })
  )

  const committed = useRef<Cache<T>>()
  const prevCache = committed.current

  let cache = prevCache
  if (cache) {
    const useCache = Boolean(
      inputs && cache.inputs && areInputsEqual(inputs, cache.inputs)
    )
    if (!useCache) {
      cache = {
        inputs,
        result: getResult(),
      }
    }
  } else {
    cache = initial
  }

  useEffect(() => {
    committed.current = cache
    if (prevCache == initial) {
      initial.inputs = initial.result = undefined
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [cache])

  return cache.result!
}

function areInputsEqual(next: any[], prev: any[]) {
  if (next.length !== prev.length) {
    return false
  }
  for (let i = 0; i < next.length; i++) {
    if (next[i] !== prev[i]) {
      return false
    }
  }
  return true
}
/* eslint-disable react-hooks/exhaustive-deps */
import { useEffect, EffectCallback } from 'react'

export const useOnce = (effect: EffectCallback) => useEffect(effect, emptyDeps)

const emptyDeps: any[] = []
import { useEffect, useRef } from 'react'

/** Use a value from the previous render */
export function usePrev<T>(value: T): T | undefined {
  const prevRef = useRef<any>()
  useEffect(() => {
    prevRef.current = value
  })
  return prevRef.current
}
import { useState } from 'react'

import { assign } from '../globals'
import { useIsomorphicLayoutEffect } from './useIsomorphicLayoutEffect'

/**
 * Returns `boolean` or `null`, used to automatically
 * set skipAnimations to the value of the user's
 * `prefers-reduced-motion` query.
 *
 * The return value, post-effect, is the value of their prefered setting
 */
export const useReducedMotion = () => {
  const [reducedMotion, setReducedMotion] = useState<boolean | null>(null)

  useIsomorphicLayoutEffect(() => {
    const mql = window.matchMedia('(prefers-reduced-motion)')

    const handleMediaChange = (e: MediaQueryListEvent | MediaQueryList) => {
      setReducedMotion(e.matches)

      assign({
        skipAnimation: e.matches,
      })
    }

    handleMediaChange(mql)

    mql.addEventListener('change', handleMediaChange)

    return () => {
      mql.removeEventListener('change', handleMediaChange)
    }
  }, [])

  return reducedMotion
}
