// App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import ViewPage from "./component/ViewPage";
import BoardView from "./component/BoardView";
import ListView from "./component/ListView";
import CalendarView from "./component/CalendarView";
import Login from "./component/Login";
import Register from "./component/Register";

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    // Perform login logic here, e.g., check credentials, set token, etc.
    // For simplicity, we'll just set isLoggedIn to true.
    setLoggedIn(true);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={<ViewPage view={<ListView />} index={1} />}
          />
          {isLoggedIn ? (
            <>
              {/* <Route
                path="/"
                // element={<ViewPage view={<ListView />} index={1} />}
                element={<Login />}
              /> */}
              <Route
                path="/ListView"
                element={<ViewPage view={<ListView />} index={1} />}
              />
              <Route
                path="/Board"
                element={<ViewPage index={3} view={<BoardView />} />}
              />
              <Route
                path="/Calendar"
                element={<ViewPage index={2} view={<CalendarView />} />}
              />
            </>
          ) : (
            <>
              <Route path="/" element={<Login onLogin={handleLogin} />} />
              <Route path="/register" element={<Register />} />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
