import Grid from "@mui/material/Grid";
import { Box, Card, CardContent, Typography } from "@mui/material";
import { useEffect, useState } from "react";
import axios from "axios";
export default function Admin() {
  const [data, setData] = useState([]);
  useEffect(() => {
    // getTasks();
    axios
      .get("http://localhost:3002/api/tasks/admin")
      .then((res) => {
        setData(res.data);
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  const styles = {
    gridContainer: {
      display: "grid",
      placeItems: "center",
    },
    table: {
      borderCollapse: "collapse",
      width: "100%",
    },
    th: {
      border: "1px solid black",
      padding: "8px",
      textAlign: "left",
      backgroundColor: "",
    },
    td: {
      border: "1px solid black",
      padding: "8px",
      textAlign: "left",
    },
  };
  console.log(data?.data?.map((e) => e));
  const user = [...new Set(data?.data?.map((e) => e.username))];
  // const filteredData = data?.data?.filter((item) => item.username === user);

  const titleCounts = {};

  // Initialize titleCounts with 0 for each user
  user.forEach((user) => {
    titleCounts[user] = 0;
  });

  // Count titles for each user
  data?.data.forEach((item) => {
    if (user.includes(item.username)) {
      titleCounts[item.username]++;
    }
  });
  console.log(titleCounts);
  const formattedTitleCounts = Object.keys(titleCounts).map((name) => ({
    name,
    total: titleCounts[name],
  }));
  return (
    <div>
      <h1>Admin</h1>
      <Grid
        container
        spacing={2}
        alignItems="stretch"
        style={{
          border: "1px solid white",
          marginTop: "5px",
        }}
      >
        <Grid item xs={4}>
          <Card
            variant="outlined"
            sx={{ backgroundColor: "#2b2f33", borderRadius: 2 }}
          >
            <CardContent>
              <Grid container spacing={2} alignItems="stretch">
                <Grid item xs={8} sx={{ p: 2 }}>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-center",
                      backgroundColor: "",
                    }}
                  >
                    <h2>Users</h2>
                  </Box>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-start",
                      backgroundColor: "",
                      mt: 2,
                    }}
                  ></Box>
                </Grid>
                <Grid item xs={4}>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-end",
                      backgroundColor: "",
                    }}
                  >
                    {/* <h2>
                      <i className="fa-solid fa-list-check"></i>
                    </h2> */}
                  </Box>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={4}>
          <Card
            variant="outlined"
            sx={{ backgroundColor: "#2b2f33", borderRadius: 2 }}
          >
            <CardContent>
              <Grid container spacing={2} alignItems="stretch">
                <Grid item xs={8} sx={{ p: 2 }}>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-center",
                      backgroundColor: "",
                    }}
                  >
                    <h2>Complete Tasks</h2>
                  </Box>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-start",
                      backgroundColor: "",
                      mt: 2,
                    }}
                  ></Box>
                </Grid>
                <Grid item xs={4}>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-end",
                      backgroundColor: "",
                    }}
                  >
                    {/* <h2>
                      <i className="fa-solid fa-clipboard-list"></i>
                    </h2> */}
                  </Box>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={4}>
          <Card
            variant="outlined"
            sx={{ backgroundColor: "#2b2f33", borderRadius: 2 }}
          >
            <CardContent>
              <Grid container spacing={2} alignItems="stretch">
                <Grid item xs={8} sx={{ p: 2 }}>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-center",
                      backgroundColor: "",
                    }}
                  >
                    <h2>Incomplete Tasks</h2>
                  </Box>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-start",
                      backgroundColor: "",
                      mt: 2,
                    }}
                  ></Box>
                </Grid>
                <Grid item xs={4}>
                  <Box
                    sx={{
                      display: "flex",
                      justifyContent: "flex-end",
                      backgroundColor: "",
                    }}
                  >
                    {/* <h2>
                      <i class="fa fa-list-alt" aria-hidden="true"></i>
                    </h2> */}
                  </Box>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      <Grid
        container
        spacing={2}
        alignItems="stretch"
        style={{
          border: "1px solid white",
          marginTop: "20px",
          marginBottom: "5px",
        }}
      >
        <Grid item xs={12}>
          <div style={styles.gridContainer}>
            {/* {data?.data?.map((e) => ( */}
            <table style={styles.table}>
              <tr>
                {/* <th style={styles.th}>ID</th> */}
                <th style={styles.th}>Username</th>
                <th style={styles.th}>Total Tasks</th>
                {/* <th style={styles.th}>Incomplete Tasks</th> */}
              </tr>
              {formattedTitleCounts?.map((e) => (
                <tr>
                  {/* <td style={styles.td}>{e._id}</td> */}
                  <td style={styles.td}>{e.name}</td>
                  <td style={styles.td}>{e.total}</td>
                  {/* <td style={styles.td}></td> */}
                </tr>
              ))}
            </table>
            {/* ))} */}
          </div>
        </Grid>
      </Grid>
    </div>
  );
}
