import React from "react";
import "./Portfolio.css";
import { Link } from "react-router-dom";
import { useState } from "react";
// import "react-bootstrap";
// import "bootstrap/dist/css/bootstrap.min.css";
import informedDecisionImage from "./img/undraw_informed_decision_p2lh.svg";
export default function Portfolio() {
  const [isHovered, setIsHovered] = useState(false);
  const style = {
    margin: 5,
    fontSize: "1.3rem",
    fontWeight: "bold",
    // textDecoration: "none",
  };
  const st = {
    textDecoration: "none",
    backgroundColor: isHovered ? "lightgray" : "transparent",
  };
  const containerStyle = {
    border: "1px solid red",
    display: "flex",
    justifyContent: "space-between",
  };
  const columnStyle = {
    width: "calc(50% - 5px)", // Subtracting the border width from 50% to maintain even space between columns
  };
  return (
    <div className="portfolio">
      <div
        className="head"
        style={{
          //   border: "1px solid white",
          display: "flex",
          justifyContent: "space-around",
        }}
      >
        <h2 style={{ marginRight: "800px" }}>DEVOPS Pvt LTD</h2>
        <ul style={{ display: "flex" }}>
          <li className="" style={style}>
            <a
              style={st}
              href=""
              className="home"
              //   onMouseEnter={() => setIsHovered(true)}
              //   onMouseLeave={() => setIsHovered(false)}
            >
              Home
            </a>
          </li>
          <li style={style}>
            <a style={st} href="">
              About
            </a>
          </li>
          <li style={style}>
            <a style={st} href="">
              Service
            </a>
          </li>
          <li style={style}>
            <a style={st} href="">
              Contact
            </a>
          </li>
          <li style={style}>
            <a style={st} href="">
              <Link style={st} to="/login">
                Login
              </Link>
            </a>
          </li>
        </ul>
      </div>
      <div style={containerStyle}>
        <div style={columnStyle}>
          {/* <p>psatdfadsjbkhfb jhsdbfksydgfsd</p> */}
        </div>
        <div style={columnStyle}>
          {/* <p>jfgbskeudfgwuglui</p> */}
          <img src={informedDecisionImage} />
        </div>
      </div>
    </div>
  );
}
